import {useState,useEffect} from "react"

import axios from "axios"

//useEffec-for did mount didupdate
function Login(props){
      useEffect(()=>{
          //alert("mounted and updated")
      },{})

      var [error,setError]=useState() 
      var[users,setUser]=useState({})
      var user={} 

     let getEmail=function(event){

        user.email = event.target.value
        /*setUser({
            email:event.target.value,
            password:users.password
        }) */
     }

     let getPassword=function(event){

        user.password = event.target.value
      /*  setUser({
            email:users.email,
            password:event.target.value
        }) */
     }





    function login (){
            if(!user.email || !user.password){
                setError("Please fill all details")
               console.log(user)
                //call the method
              //  props.informlogin("AS")

            }
            else
            {

                let apiurl="https://apibyashu.herokuapp.com/api/login"
            axios({
                url:apiurl,
                method:"post",
                data:user

            }).then((response)=>{
                console.log("response from api",response.data)
                setError("Login Success")
                props.informlogin(response.data.name)
            },(error)=>{
                console.log("Error from api",error)
                setError(error)
            })

            }
     }

    return(
        <div style={{width:"50%" ,margin:"auto"}}>


        <div className="form-group">

        <label >Email</label><br/>
        <input class="form-control" placeholder="Enter Email" onChange={getEmail}></input><br/>
        {users.email}
        <label>Password</label><br/>
        <input class="form-control" placeholder="Enter Password" onChange={getPassword}></input><br/>
        {users.password}
        <button onClick={login} className="btn btn-primary">Login</button>


        <div style={{color:"red"}}>
            {error}
        </div>
        </div>

    </div>
    )
}

export default Login
