//class component with state feature to update page without affecting other component
import {Component} from "react"
import validator from 'validator'
import axios from "axios"


class SignUp extends Component{

constructor(){
    super()
    this.state={

    }
}

    user={}

  /* componentDidMount(){
        alert("mounted")
   } 

   componentDidUpdate(){
       alert("updated")
   } */

   getName=(event)=>{
       if(!event.target.value){
        this.setState({
            errorMessage:"Please fill name"
        })
       }
       else {

        this.user.name = event.target.value
        this.setState({
            errorMessage:""
        })
       }
}

getEmail=(event)=>{
    if(!event.target.value){
        this.setState({
            errorMessage:"Please fill email"
        })
       }
      else{
     this.user.email = event.target.value
     this.setState({
        errorMessage:""
       })
      }
}

getPassword=(event)=>{
    if(!event.target.value){
        this.setState({
            errorMessage:"Please fill password"
        })
       }
       else{
    this.user.password=event.target.value
    this.setState({
        errorMessage:""
    })
       }
}

Register=()=>{
     if(!this.user.name && !this.user.password && !this.user.email){
         this.setState({
             errorMessage:"Please fill details"
         })
     }
         else{
             if(!validator.isEmail(this.user.email)){
                this.setState({
                    errorMessage:"Please enter valid email"
                })
                document.getElementById("name").value=""
                  document.getElementById("email").value=""
                  document.getElementById("password").value=""
             }
             else{
                let apiurl="https://apibyashu.herokuapp.com/api/register"
                axios({
                    url:apiurl,
                    method:"post",
                    data:this.user

                }).then((response)=>{
                    console.log("response from api",response.data)
                    this.setState({
                        errorMessage:response.data.message
                    })
                },(error)=>{
                    console.log("Error from api",error)
                    this.setState({
                        errorMessage:error
                    })
                })

             }



         }
     }

     render(){
        return(
            <div style={{width:"50%" ,margin:"auto"}}>

                <form>
                <div className="form-group">
                <label >Name</label><br/>
                <input type="text" class="form-control" id="name" required placeholder="Enter Name" onChange={this.getName}></input><br/>

                <label >Email</label><br/>
                <input type="email" class="form-control" id="email" required placeholder="Enter Email" onChange={this.getEmail}></input><br/>
                <label>Password</label><br/>
                <input type="password" class="form-control" id="password" required placeholder="Enter Password" onChange={this.getPassword}></input><br/>
                <button onClick={this.Register} className="btn btn-primary">Register</button>
                <div style={{color:"red"}}>
                    {this.state.errorMessage}
                </div>
                </div>
                </form>

            </div>
        )
     }


}



export default SignUp
