import {createStore} from 'redux'
import { demo } from "./reducers";

var dmart=createStore(demo)

dmart.dipatch({
    type:"login"
})

console.log("....",dmart.getState())

dmart.dispatch({
    type:"LOGIN",
    payload:{email:"abc@gmail.com",name:"akash"}
})

console.log("....",dmart.getState())

export default dmart
