import logo from './logo.svg';
import './App.css';
import NavBar from './NavBar.js'
import Carousel from './carousel.js'
import Card from './Card'
import SignUp from './Signup.js'
import  Login from './Login.js'
import   cakes  from './data.js'
import { useEffect, useState } from 'react';
import CakeDetail from './CakeDetail.js'
import axios from 'axios';



function App() {


  var [login,setLogin]=useState(false)

  var [user,setUser]=useState()

    var[cakes,setCakes]=useState()
    var allcakeurl="https://apibyashu.herokuaoo.com/api/allcakes"
useEffect(()=>{
  axios({
    method:"get",
    url:allcakeurl
  }).then((response)=>{
    console.log("response from all cake api",response.data)
    setCakes(response.data.data)
},(error)=>{
    console.log("Error from all cake api",error)
})
})  




function LoginDone(data){
  setUser(data)
  setLogin(true)
}



  return (
    <div className="App">

      <Login  informlogin={LoginDone}/>
      <SignUp></SignUp>
     <NavBar islogin={login} setlogin={setLogin}  user={user}/>
     <Carousel></Carousel>



    <div className="row">
      {cakes?.length>0 && cakes.map((each,index)=>{
        return(<Card countrydata={each} key={index} />)
      })}
    </div>

    <CakeDetail ></CakeDetail>

    </div>
  );
}

export default App;
