import { useEffect, useState } from 'react'

import axios from 'axios'

function Cart(props){
    let token=localStorage.token
    var[cakes,setCakes]=useState()
    var cakecarturl="https://apibyashu.herokuapp.com/api/cakecart"
 useEffect(()=>{

    axios({
        url:cakecarturl,
        method:'post',
        headers:{
          authtoken:token
        },
        data:{}
      }).then((response)=>{
        console.log("cake data",response.data)
        setCakes(response.data.data)
        console.log("cakes is ",cakes) 
      },(error)=>{
        console.log(error)
      })
})

{cakes?.length>0 && cakes.map((each,index)=>{
    return(<Cart cakedata={each} key={index} />)
  })}

    return(
        <div class="container mt-5 mb-5">
        <div class="d-flex justify-content-center row">
            <div class="col-md-8">
                <div class="p-2">
                    <h1>My cart</h1>
                </div>
                <div class="d-flex flex-row justify-content-between align-items-center p-2 bg-white mt-4 px-3 rounded">
                    <div class="mr-1"><img class="rounded" src="https://i.imgur.com/XiFJkhI.jpg" width="70"/></div>
                    <div class="d-flex flex-column align-items-center product-details"><span class="font-weight-bold">Basic T-shirt</span>
                        <div class="d-flex flex-row product-desc">
                            <div class="size mr-1"><span class="text-grey">cakes.price</span><span class="font-weight-bold">&nbsp;M</span></div>
                            <div class="color"><span class="text-grey">Color:</span><span class="font-weight-bold">&nbsp;Grey</span></div>
                        </div>
                    </div>
                    <div class="d-flex flex-row align-items-center qty"><i class="fa fa-minus text-danger"></i>
                        <h5 class="text-grey mt-1 mr-1 ml-1">{props.cakedata.name}</h5><i class="fa fa-plus text-success"></i>
                    </div>
                    <div>
                    <button class="btn btn-warning " type="button">Remove</button>
                    </div>
                    <div class="d-flex align-items-center"><i class="fa fa-trash mb-1 text-danger"></i></div>
                </div>



                <div class="d-flex flex-row align-items-center mt-3 p-2 bg-white rounded"><button class="btn btn-warning btn-block btn-lg ml-2 pay-button" type="button">Proceed to Pay</button></div>
            </div>
        </div>
    </div>




    )
}

export default Cart
