import { Link } from 'react-router-dom'
import { useState } from 'react'
import { connect } from 'react-redux'
import * as Icon from 'react-bootstrap-icons';

function NavBar(props) {

  let [serchText,setSerchText]=useState()




/*
  let onLogin=()=>{
    props.setlogin(true)
}*/

let onLogout=()=>{
    //props.setlogin(false)
    //localStorage.token =""
    props.dispatch({
      type:"LOGOUT",



    })
    localStorage.token=""
}

let getText=(event)=>{

   setSerchText(event.target.value)
}

    return (

      <div className="NavBar">

        <nav class="navbar navbar-expand-lg navbar-light bg-light">
    <Link to="/"> <a class="navbar-brand" href="#">The Cake Shop</a> </Link>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>

  <div class="collapse navbar-collapse" id="navbarSupportedContent">
    <ul class="navbar-nav mr-auto">
      <li class="nav-item active">

     {  props.user && <a class="nav-link" href="#">Welcome {props.user} <span class="sr-only">(current)</span></a>
     }


      </li>



    </ul>
    <form class="form-inline my-2 my-lg-0">
      <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" onChange={getText}/>
     <Link to={'/search/search?cakes='+serchText}> <Icon.Search  className="mr-2" size={30}/></Link> 

      {props.islogin ?
      <div>
        <Link to={'/cart'}> <Icon.Cart2 className="mr-2" size={30}/></Link>
      <button onClick={onLogout} className="btn btn-primary mr-1">Logout</button> </div>:<Link to="/login"><button  className="btn btn-primary mr-1">Login</button></Link>}

    </form>
  </div>
</nav>
      </div>
    );
  }

  export default connect(function(state,props){

    return{
      user:state && state["user"]["name"],
      islogin :state && state["isloggedin"]
    }
  })(NavBar);
