var demo = function(state,action){
    switch(action.type){
        case "LOGIN":{
            console.log("login is successfull")
            state={...state}
            state["isloggedin"]=true
            state["user"]=action.payload
            alert(state.user.name)
            return state
        }
        case "LOGOUT":{

            state={...state}
            state["isloggedin"]=false
            state["user"]=""

            return state
        }
        case "ALREADY_LOG_IN":{
            console.log("Already log in")
            state={...state}
            state["isloggedin"]=true
            state["user"]=action.payload

            return state
        }


        default:return state
    }
}
export default demo
