 useEffect(()=>{

    axios({
        url:cakecarturl,
        method:'post',
        headers:{
          authtoken:token
        },
        data:{}
      }).then((response)=>{
        console.log("cake data",response.data)
        setCakes(response.data.data)
        console.log("cakes is ",cakes) 
      },(error)=>{
