//class component with state feature to update page without affecting other component
import {Component} from "react"

import axios from "axios"


class SignUp extends Component{

constructor(){
    super()
    this.state={

    }
}

    user={}

  /* componentDidMount(){
        alert("mounted")
   } 

   componentDidUpdate(){
       alert("updated")
   } */

   getName=(event)=>{
       if(event.target.value){
        this.setState({
            errorMessage:"Please fill name"
        })
       }
       else {

        this.user.name = event.target.value
        this.setState({
            errorMessage:""
        })
       }
}

getEmail=(event)=>{
    if(event.target.value){
        this.setState({
            errorMessage:"Please fill email"
        })
       }
      else{
     this.user.email = event.target.value
     this.setState({
        errorMessage:""
       })
      }
}

getPassword=(event)=>{
    this.user.password=event.target.value
}

Register=()=>{
     if(!this.user.email || !this.user.password|| !this.user.name){
         this.setState({
             errorMessage:"Please fill details"
         })
     }
         else{

            let apiurl="https://apibyashu.herokuapp.com/api/register"
            axios({
                url:apiurl,
                method:"post",
                data:this.user

            }).then((response)=>{
                console.log("response from api",response.data)
            },(error)=>{
                console.log("Error from api",error)
            })

         }
     }

     render(){
        return(
            <div style={{width:"50%" ,margin:"auto"}}>


                <div className="form-group">
                <label >Name</label><br/>
                <input type="text" class="form-control" placeholder="Enter Name" onChange={this.getName}></input><br/>

                <label >Email</label><br/>
                <input type="email" class="form-control" placeholder="Enter Email" onChange={this.getEmail}></input><br/>
                <label>Password</label><br/>
                <input type="password" class="form-control" placeholder="Enter Password" onChange={this.getPassword}></input><br/>
                <button onClick={this.Register} className="btn btn-primary">Register</button>
                <div style={{color:"red"}}>
                    {this.state.errorMessage}
                </div>
                </div>

            </div>
        )
     }


}



export default SignUp
