//import logo from './logo.svg';
import './App.css';
import CakeDetail from './CakeDetail';
import NavBar from './NavBar.js'

import SignUp from './Signup.js'
import Checkout from './Checkout.js'
import Home from './Home.js'
import  Login from './Login.js'
import  PageNotFound from './PageNotFound'
//import   cakes  from './data.js'
import { useEffect, useState } from 'react';

import {BrowserRouter as Router, Redirect, Route, Switch} from 'react-router-dom'
import CakeSearch from './CakeSearch';
import Cart from './Cart';
import axios from 'axios';
import { connect } from 'react-redux';
//import CakeDetail from './CakeDetail';
import dmart from './reduxstore/store'


function App(props) {

 if(localStorage.token && !props.user){
   var token=localStorage.token
   console.log("already log in")
   axios({
     method:'get',
     url:"https://apibyashu.herokuapp.com/api/getuserdetails",
     headers:{
       authtoken:token
     }

   }).then((response)=>{
        console.log("data",response)
        props.dispatch({
          type:"ALREADY_LOG_IN",
          payload:response.data.data
        })
   },(error)=>{
        console.log("error",error)
   })



 }







  return (

    <div className="App">

     <Router>
      <NavBar  /> 
       <div>
         <Switch>
         <Route path="/" exact component={Home}/>
          { !props.islogin & !localStorage.token ? <Route path="/login" exact component><Login /></Route>:
           <Redirect to="/"></Redirect>
          }



         <Route path="/signup" exact component={SignUp}/>
         <Route path="/search/:text" exact component={CakeSearch}/>
         <Route path="/cake/:cakeid" exact component={CakeDetail}/>


         {  dmart.isloggedin ? <Route path="/cart" exact component={Cart}/>:
           <Redirect to="/"></Redirect>
          }

         {  props.islogin ?  <Route path="/checkout"  component={Checkout}/>:
           <Redirect to="/"></Redirect>
          }




         <Route path="/" exact component={Home}/>





         <Route path="/*" >
           <Redirect to="/"></Redirect>
         </Route> 
         </Switch>
       </div>
     </Router>



    </div> 


  );
}

export default connect(function(state,props){
return{
  user:state?.user,
  islogin :state && state["isloggedin"]
}
})(App);
