import { Route } from "react-router"
import { Link } from "react-router-dom"
import Address from "./Address.js"
import CartSummary from "./CartSummary.js"
import Order from "./Order.js"
import Payment from "./Payment.js"
import {useRouteMatch} from 'react-router-dom'
import { connect } from "react-redux"

function Checkout(){
    var route= useRouteMatch()
    var url=route.url
    var path=route.path

    return(
    <div className="row">

    <div className="col-4">
    <ul class="list-group">
{ props.access <= 4 &&  props.access >= 1 &&   <Link to={url}>  <li class="list-group-item ">Cart Summary</li></Link>
}
{ props.access <= 4  && props.access > 1 &&     <Link to={url + '/address'}>   <li class="list-group-item" > Address</li></Link>
}     
{props.access <= 4  && props.access > 2 && <Link to={url + '/payment'}>  <li class="list-group-item"> Payment</li></Link>
}     
{props.access == 4  && <Link to={url + '/order'}>  <li class="list-group-item">Order</li></Link>
}     </ul>




    </div>
    <div className="col-8">
        <Route  exact path={path}  component={CartSummary}/>
        <Route  exact path= {path + '/address'}  component={Address}/>
        <Route   exact path={path + '/payment'}  component={Payment}/>
        <Route   exact path={path + '/order'}  component={Order}/>
    </div>


    </div>
    )
}
export default connect(function (state,props) {
    return{

    access:state?.access
    }
})(Checkout)
