import { useSelector } from "react-redux"


function Address (){
var [formerror,setFormerrors]=useState()


 let validate=(elements)=>{
          console.log("received element",elements,elements.name)
          var errors = {}
          if(!elements.name.value){
            errors.name="Name is Required"
          }
          var errorkeys=Object.keys(errors)
          console.log(errors,errorkeys)
          if(errorkeys.length>0)
          return errors
          else
          return false
 }

  let placeOrder=()=>{
    var form =document.getElementById("addressform")
    console.log("Form elements ",form.elements , form.controls)
    var error = validate(form.elements)
    console.log(error)
    if(error){
      console.log(error)
      setFormerrors(error)
      //console.log(formerror)
    }

}



