import NavBar from './NavBar.js'
import Carousel from './carousel.js'
import Card from './Card'
import { useEffect, useState } from 'react';
import CakeDetail from './CakeDetail.js'
import axios from 'axios';
import CakeSearch from './CakeSearch.js'
import  Login from './Login.js'
import SignUp from './Signup.js'


function Home (){
    var [login,setLogin]=useState(false)

  var [user,setUser]=useState()

    var[cakes,setCakes]=useState()
    var allcakeurl="https://apibyashu.herokuapp.com/api/allcakes"
useEffect(()=>{
  axios({
    method:"get",
    url:allcakeurl
  }).then((response)=>{
    console.log("response from all cake api",response.data.data)
    setCakes(response.data.data)
},(error)=>{
    console.log("Error from all cake api",error)
})
})

function LoginDone(data){
    setUser(data)
    setLogin(true)
  }

    return(
        <div className="Home">
        <SignUp></SignUp> 

        <Carousel></Carousel>

         <CakeSearch></CakeSearch>

       <div className="row">
      {cakes?.length>0 && cakes.map((each,index)=>{
        return(<Card countrydata={each} key={index} />)
      })}
      </div>

         <CakeDetail ></CakeDetail>
        </div>

    )
}

export default Home
