import {useParams} from 'react-router-dom'
import { useEffect, useState } from 'react'
import axios from 'axios'
function CakeDetail(){

  let [cakedata,setCakedata] = useState({})  

  let [addcart,setCartdata] = useState()  

  let params =useParams()



  useEffect(()=>{
    let cakedetailapiurl="https://apibyashu.herokuapp.com/api/cake/"+params.cakeid
    axios({
      method:'get',
      url:cakedetailapiurl,
    }).then((response)=>{
            setCakedata(response.data.data)

    },(error)=>{
         console.log("Error:-",error)
    })
  })


  let addToCart=()=>{

      let addtocarturl="https://apibyashu.herokuapp.com/api/addcaketocart"
      let token=localStorage.token
      axios({
        url:addtocarturl,
        method:'post',
        headers:{
          authtoken:token
        },
        data:{
          cakeid:cakedata.cakeid,
          name:cakedata.name,
          image:cakedata.image,
          price:cakedata.price,
          weight:cakedata.weight
        }
      }).then((response)=>{
        console.log("cake isss data",response.data)
        setCartdata("Item Added to cart successfully..")
      },(error)=>{
        console.log(error)
      })


  }

    return(
        <div class="card mb-3" style={{'width': 'Auto' ,margin:'30px'}}>
        <div class="row g-0">
          <div class="col-md-4">
            <img style={{margin:'20px' ,height:'500px'}}
              src={cakedata.image}

              alt="..."
              class="img-fluid"
            />
          </div>
          <div class="col-md-8">
            <div class="card-body">
              <h5 class="card-title">{cakedata.name}</h5><br/>
              <h6 class="card-text">
              {cakedata.description}
              </h6>
              <h3 class="card-text">
               CURRENT PRICE : <label style={{color:'orange'}}> {cakedata.price}</label>
              </h3>
              <h6 class="card-text">
                <b>91%</b> of buyers enjoyed this product <b>(87 votes)</b>
              </h6>

              <h3 class="card-text">
                <b>Weight:  3KG</b> 
              </h3>
              <h3 class="card-text">
               FLAVOUR : <label style={{color:'orange'}}> HAZELNUT CAKE</label>
              </h3>

              <h6 class="card-text">
                <b>TYPE</b> 
              </h6>
              <h6 class="card-text">
               GENERAL
              </h6>
              <button type="button" class="btn btn-warning" onClick={addToCart}>Add to Cart</button>
             <br/>
             <br/>
             { addcart && <div class="alert alert-warning alert-dismissible fade show"  role="alert">
            {  addcart}
            <button type="button" class="close" data-dismiss="alert" aria-label="Close">
            <span aria-hidden="true">&times;</span>
            </button>
              </div> }

            </div>
          </div>
        </div>
      </div>
    )
}

export default CakeDetail
