getPassword=(event)=>{
    this.user.password=event.target.value
}

Register=()=>{
     if(!this.user.email || !this.user.password){
         this.setState({
             errorMessage:"Please fill details"
         })
     }
         else{

            if(this.user.email=="abc@abc.com" && this.user.password=="123"){
                this.setState({
                    errorMessage:"Login Success"
                })
            }
            else{
            this.setState({
                errorMessage:"Invalid user or password"
            })
           }
         }
     }

     render(){
        return(
            <div style={{width:"50%" ,margin:"auto"}}>


                <div className="form-group">
                <label >Email</label><br/>
                <input class="form-control" placeholder="Enter Email" onChange={this.getEmail}></input><br/>
                <label>Password</label><br/>
                <input class="form-control" placeholder="Enter Password" onChange={this.getPassword}></input><br/>
                <button onClick={this.Register} className="btn btn-primary">Register</button>
                <div style={{color:"red"}}>
                    {this.state.errorMessage}
                </div>
                </div>

            </div>
        )
     }


}



export default SignUp
