import {useState,useEffect} from "react"

import axios from "axios"
import { Link ,withRouter} from "react-router-dom"
import { connect } from "react-redux"


//useEffec-for did mount didupdate
function Login(props){


    var [formerror,setFormerrors]=useState()


      var[users,setUser]=useState({})
      var user={} 

     let getEmail=function(event){

            user.email = event.target.value




        /*setUser({
            email:event.target.value,
            password:users.password
        }) */

     }

     let getPassword=function(event){

        user.password = event.target.value

      }


      /*  setUser({
            email:users.email,
            password:event.target.value
        }) */






    function login (){
            if(!user.email || !user.password){

                setFormerrors("Please all details")




                //call the method
              //  props.informlogin("AS")

            }
            else
            {


                let apiurl="https://apibyashu.herokuapp.com/api/login"
            axios({
                url:apiurl,
                method:"post",
                data:user

            }).then((response)=>{
                console.log("response from api",response.data)

                if(response.data.token){

                    //route using code -rote add extra feat. to props of component
                    props.history.push("/")
                    //localStorage.email=response.data.email
                    localStorage.token=response.data.token
                    //localStorage.name=response.data.name
                   // props.informlogin(localStorage.name)
                   console.log("...previous path",props.url)
                    props.dispatch({
                        type:"LOGIN",
                        payload:{email:response.data.email,name:response.data.name}
                    }) 
                }
                else{
                    setFormerrors(response.data.message)
                }

               //props.informlogin(response.data.name)
            },(error)=>{
                console.log("Error from api",error)
                setFormerrors(error)
            })


          }

        }

    return(
        <div style={{width:"50%" ,margin:"auto"}}>

         <form id="loginform">       
        <div className="form-group">

        <label >Email</label><br/>
        <input class="form-control" name="email" type="email" placeholder="Enter Email" onChange={getEmail}></input><br/>
        {users.email}
        <label>Password</label><br/>
        <input class="form-control" name="password" type="password" placeholder="Enter Password" onChange={getPassword}></input><br/>
        {users.password}
       <button onClick={login} className="btn btn-primary">Login</button>
       <div style={{float:"left"}}>
       <Link to="/signup"> Not a user?</Link>
       </div>
       <div style={{float:"right"}}>
       <Link to="/forgotpassword"> Forgot Password ?</Link>
       </div>

        <div style={{color:"red"}}>
            {formerror}
        </div>
        </div>
        </form>

    </div>
    )
}

Login=withRouter(Login)
export default connect(function(state,props){
    console.log(".....state",state)
    return{

      url :state && state["url"]
    }
  })(Login)
