import axios from "axios"
import {call , action}  from "redux-saga"
import {takeEvery ,  put} from "redux-saga"

function login(action){
    return axios({
        method:"post",
        url:"https://apibyashu.herokuapp.com/api/login",
        data:action.payload
    })
}

export function* RootSaga(){
 yield takeEvery('LOGIN',LoginGenerator)
}

function* LoginGenerator(){
    var result=yield call(login,action)

    if(result.data.token){
        yield put({
            type:'Login_Success',
            payload:result.data
        })

    }
    else{
        yield put({
            type:'Login_Fail'
           // payload:result.data
        })
    }

}
