import { Link } from 'react-router-dom'
import { useState } from 'react'
import { connect } from 'react-redux'

function NavBar(props) {

  let [serchText,setSerchText]=useState()




/*
  let onLogin=()=>{
    props.setlogin(true)
}*/

let onLogout=()=>{
    //props.setlogin(false)
    //localStorage.token =""
    props.dispatch({
      type:"LOGOUT"
    })
}

let getText=(event)=>{

   setSerchText(event.target.value)
}

    return (

      <div className="NavBar">

        <nav class="navbar navbar-expand-lg navbar-light bg-light">
    <Link to="/"> <a class="navbar-brand" href="#">The Cake Shop</a> </Link>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>

  <div class="collapse navbar-collapse" id="navbarSupportedContent">
    <ul class="navbar-nav mr-auto">
      <li class="nav-item active">

     {  props.user && <a class="nav-link" href="#">Welcome {localStorage.name} <span class="sr-only">(current)</span></a>
     }


      </li>
      <li class="nav-item">
        <a class="nav-link" href="#">Link</a>
      </li>
      <li class="nav-item dropdown">
        <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          Dropdown
        </a>
        <div class="dropdown-menu" aria-labelledby="navbarDropdown">
          <a class="dropdown-item" href="#">Action</a>
          <a class="dropdown-item" href="#">Another action</a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item" href="#">Something else here</a>
        </div>
      </li>
      <li class="nav-item">
        <a class="nav-link disabled" href="#" tabindex="-1" aria-disabled="true">Disabled</a>
      </li>
    </ul>
    <form class="form-inline my-2 my-lg-0">
      <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" onChange={getText}/>
     <Link to={'/search/search?cakes='+serchText}> <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button></Link> 
      {props.islogin ?<button onClick={onLogout} className="btn btn-primary">Logout</button>:<Link to="/login"><button onClick={onLogin} className="btn btn-primary">Login</button></Link>}

    </form>
  </div>
</nav>
      </div>
    );
  }

  export default connect(function(state,props){
    console.log(".....state",state)
    return{
      user:state && state["user"]["name"],
      islogin :state && state["isloggedin"]
    }
  })(NavBar);
